scss:
.input {
  padding: $spacing-sm;
  border: 1px solid $color-border;
  border-radius: $radius;
  font-size: $font-size-base;
  &--sm { font-size: 0.875rem; }
  &--md { font-size: 1rem; }
  &--lg { font-size: 1.125rem; }
  &:disabled { background-color: $color-disabled-bg; cursor: not-allowed; }
}
