scss:
.site-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: $spacing-md;
  &__logo img {
    height: 40px;
  }
  &__nav ul {
    list-style: none;
    display: flex;
    gap: $spacing-sm;
    a {
      color: $color-text;
      text-decoration: none;
      &:hover {
        color: $color-primary;
      }
    }
  }
}
